    int n4 = sizeof(d)/sizeof(d[0]);
    quicksort(d, 0, n4-1);
    printf("Test 4: ");
    for(int i=0; i<n4; i++) printf("%d ", d[i]); printf("\n");

    // ✅ 测试 5：空数组（n=0，不调用，安全）
    int e[] = {};
